from pygments.style import Style
from pygments.token import Keyword, Name, Comment, String, Error, \
     Number, Operator, Generic, Token, Whitespace


class OneDarkStyle(Style):
    """
    Pygments version of the "native" vim theme.
    """

    # background_color = '#202020'
    background_color = '#262a31'
    highlight_color = '#404040'
    line_number_color = '#aaaaaa'

    styles = {
        # Token:              '#d0d0d0',
        Token:              '#abb2bf',
        Whitespace:         '#666666',

        # Comment:            'italic #999999',
        Comment:            'italic #5c6370',
        Comment.Preproc:    'noitalic bold #cd2828',
        Comment.Special:    'noitalic bold #e50808 bg:#520000',

        # Keyword:            'bold #6ab825',
        Keyword:            'bold #c678dd',
        # Keyword.Pseudo:     'nobold',
        Keyword.Pseudo:     'nobold #61afef',
        # Operator.Word:      'bold #6ab825',
        Operator:           'bold #e5c07b',
        Operator.Word:      'bold #c678dd',

        # String:             '#ed9d13',
        String:             '#98c27a',
        String.Other:       '#ffa500',

        Number:             'bold #d19a66',

        Name.Builtin:       '#e5c07b',
        # Name.Variable:      '#40ffff',
        Name.Variable:      '#e5c07b',
        Name.Constant:      '#e5c07b',
        Name.Class:         'underline #e5c07b',
        Name.Function:      '#e5c07b',
        Name.Namespace:     'underline #e5c07b',
        Name.Exception:     '#e5c07b',
        # Name.Tag:           'bold #6ab825',
        Name.Tag:           'bold #e5c07b',
        Name.Attribute:     '#e5c07b',
        Name.Decorator:     '#e5c07b',

        Generic.Heading:    'bold #e5c07b',
        Generic.Subheading: 'underline #e5c07b',
        Generic.Deleted:    '#e5c07b',
        Generic.Inserted:   '#e5c07b',
        Generic.Error:      '#e5c07b',
        Generic.Emph:       'italic',
        Generic.Strong:     'bold',
        Generic.Prompt:     '#e5c07b',
        Generic.Output:     '#e5c07b',
        Generic.Traceback:  '#e5c07b',

        Error:              'bg:#e3d2d2 #e5c07b'
    }
