from pygments.style import Style
from pygments.token import Keyword, Name, Comment, String, Error, \
     Number, Operator, Generic, Token, Whitespace


class GitHubDarkDimmedStyle(Style):
    """
    Pygments version of the "native" vim theme.
    """

    background_color = '#22272e'
    highlight_color = '#404040'
    line_number_color = '#adbac7'

    styles = {
        Token:              '#adbac7',
        Whitespace:         '#666666',

        Comment:            'italic #768390',
        Comment.Preproc:    'noitalic bold #f47067',
        Comment.Special:    'noitalic bold #f47067 bg:#768390',

        Keyword:            'bold #dcbdfb',
        Keyword.Pseudo:     'nobold #61afef',
        Operator:           'bold #f69d50',
        Operator.Word:      'bold #dcbdfb',

        String:             '#96d0ff',
        String.Other:       '#f69d50',

        Number:             'bold #f47067',

        Name.Builtin:       '#f69d50',
        Name.Variable:      '#f69d50',
        Name.Constant:      '#f69d50',
        Name.Class:         'underline #f69d50',
        Name.Function:      '#f69d50',
        Name.Namespace:     'underline #f69d50',
        Name.Exception:     '#f69d50',
        Name.Tag:           'bold #f69d50',
        Name.Attribute:     '#f69d50',
        Name.Decorator:     '#f69d50',

        Generic.Heading:    'bold #f69d50',
        Generic.Subheading: 'underline #f69d50',
        Generic.Deleted:    '#f69d50',
        Generic.Inserted:   '#f69d50',
        Generic.Error:      '#f69d50',
        Generic.Emph:       'italic',
        Generic.Strong:     'bold',
        Generic.Prompt:     '#f69d50',
        Generic.Output:     '#f69d50',
        Generic.Traceback:  '#f69d50',

        Error:              'bg:#e3d2d2 #f69d50'
    }